class Solution:
    # @param A : list of integers
    # @param B : integer
    # @return an integer
    def solve(self, A, B):

        def numodd(arr):
            num = 0
            for i in range(len(arr)):
                if arr[i] & 1 == 1:
                    num += 1
            return num
        
        odd = list()
        
        for i in range(len(A)):
            odd.append(numodd(A[: i + 1]))
        
        if odd[-1] < B:
            return 0

        ind = 0
        res = 0

        while ind < len(A):

            for i in range(ind, len(A)):
                if ind != 0:
                    if odd[i] - odd[ind - 1] == B:
                         res += 1
                else:
                    if odd[i] == B:
                        res += 1

            ind += 1
        
        return res       

